const gulp=require("gulp"),cleanCSS=require("gulp-clean-css"),terser=require("gulp-terser"),htmlmin=require("gulp-htmlmin"),paths={html:"*.html",css:"*.css",js:"*.js"};gulp.task("minify-html",(()=>gulp.src(paths.html).pipe(htmlmin({collapseWhitespace:!0,removeComments:!0})).pipe(gulp.dest("dist")))),gulp.task("minify-css",(()=>gulp.src(paths.css).pipe(cleanCSS()).pipe(gulp.dest("dist/css")))),gulp.task("minify-js",(()=>gulp.src(paths.js).pipe(terser()).pipe(gulp.dest("dist/js")))),gulp.task("watch",(()=>{gulp.watch(paths.html,gulp.series("minify-html")),gulp.watch(paths.css,gulp.series("minify-css")),gulp.watch(paths.js,gulp.series("minify-js"))})),gulp.task("default",gulp.parallel("minify-html","minify-css","minify-js"));